import { Row, Image, Form, Col } from 'react-bootstrap';
import logo from '../images/signup.png'
import {DB} from '../constants/DB'
import axios from 'axios';

const React = require('react')

class ExpenseInput extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            username: '',
            email: '',
            users: ''
        }
        this.handleSearch = this.handleSearch.bind(this)
    }

    componentDidMount(){
        let Search_URL = `${DB}/searchUser`
        axios.get(Search_URL)
            .then((response) => {
            this.setState({
                users: response.data.dataset
            })
        });
    }

    handleSearch(event){
        event.preventDefault();
        let target = event.target;
        if(target.name === "username"){
            this.setState(
                {username: target.value}
            )
        }
        if(target.name === "email"){
            this.setState(
                {email: target.value}
            )
        }
    }

    render(){
        let dates = ['Jan 31', 'Feb 20']
        return (
            <>
            <Col md={1}>
                <h6>{dates[0].split(' ')[0]}</h6>
                <h4>{dates[0].split(' ')[1]}</h4>
            </Col>
            <Col md={7}>
                <Row>
                    <Image src={category1} style={{width: "50px"}} rounded />
                    <h4 style={{paddingTop: "6px", marginLeft: "10px"}}>Grocery outlet<FontAwesomeIcon icon={faCamera} /></h4>
                </Row>
            </Col>
            <Col md={4}>
                <h6>User x</h6>
                <h4>USD15.34</h4>
            </Col>
            <p style={{border: "1px solid black", width: "100%"}}></p>
            </>
    );
  }
}

export default ExpenseInput

