import {DB} from '../constants/DB'
import axios from 'axios'
import {SET_CURRENT_USER} from '../constants/constant'
import setAuthorizationToken from '../utils/setAuthorizationToken'
import jwtDecode from 'jwt-decode'

export const setCurrentUser = (user) => {
    return{
        type: SET_CURRENT_USER,
        user
    }
}

export const login = (userData) =>{
    return dispatch => {
        return axios.post(`${DB}/signin`, userData).then(
            res=>{
                const token = res.data.token;
                localStorage.setItem('jwtToken', token)
                setAuthorizationToken(token)
                console.log(jwtDecode(token));
                // localStorage.setItem('authUser', res.data.username);
                // localStorage.setItem('authEmail', res.data.email);
                // localStorage.setItem('authID', res.data.ID);
                dispatch(setCurrentUser(jwtDecode(token)))
        });
    }
}
