import React, {useEffect, useState} from 'react'
import { Button, Form, Col, Row } from 'react-bootstrap';
import { faPen, faTimes } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import Picture from '../../components/Picture'
import {DB} from '../../constants/DB'
import Application from '../../components/application/applicaiton'

function Profile() {
    const [user, setUser] = useState();

    const [username, setUsername] = useState();
    const [email, setEmail] = useState();
    const [phone, setPhone] = useState();
    const [picture, setPicture] = useState();
    const [currency, setCurrency] = useState();
    const [time, setTime] = useState();
    const [language, setLanguage] = useState();
    const [display,setDisplay] = useState("none")

    const getAll_URL= `${DB}/getUser`
    const update_URL= `${DB}/update`
    const emailID = localStorage.getItem('authEmail');
    console.log("this is email", emailID);

    function toggleDisplay(){
        if(display === "none"){
            setDisplay("display")
            console.log("this is user: ", user);
            setUsername(user[0].username)
            setEmail(user[0].email)
            setPhone(user[0].phone)
        }
        else
            setDisplay("none")
    }

    useEffect( ()=>{
        try{
            fetch(getAll_URL, {
                method: 'post',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: emailID
                })
            }).then(res => res.json()).then(result=>{
                setUser(result.dataset)
            })
        }catch(e){
            console.log(e);
        }
    },[])

    function handleUpdate(){

    }
    // function handleUpdate(){
    //     fetch(update_URL, {
    //         method: 'post',
    //         headers: {
    //             'Accept': 'application/json',
    //             'Content-Type': 'application/json'
    //         },
    //         body: JSON.stringify({
    //             email: email,

    //         })
    //     })
    // }

    let title = ["username", "email", "phone"]
    Object.assign(displayInfo, user[0])
    let items = [];
    if(user){
        for (let number = 0; number < 3; number++) {
            items.push(
                <Form.Label>
                    {'Your ' +title[number]}
                    <Button variant="link" onClick={toggleDisplay}><FontAwesomeIcon icon={faPen} />Edit</Button><br />
                    {displayInfo[title[number]]}
                </Form.Label>
            );
            items.push(<br />)
            items.push(<br />)
        }
    }


    let others = ["Your default currency", "Your zome time", "Language"]
    let options = [['USD', 'CNY'], ['GMT-1', 'GMT-2'], ['English', 'Chinese']];
    let other_items = [];
    for (let number = 0; number < 3; number++) {
        other_items.push(
            <Form.Group controlId="exampleForm.ControlSelect1">
                <Form.Label>{others[number]}</Form.Label>
                <Form.Control as="select">
                    {
                        options[number].map((item, index) => (
                            <option key={index} >{item}</option>
                        ))
                    }
                </Form.Control>
            </Form.Group>
        );
    }

    return (
        <>
        <Row className="justify-content-md-center">
            <Col sm={2}>
                <Picture />
            </Col>
            <Col sm={2}>
                <Form.Group>
                    {items}
                </Form.Group>
            </Col>
            <Col sm={2}>
                <Form.Group controlId="exampleForm.ControlSelect1">
                    <Form.Label>Your default currency</Form.Label>
                    <Form.Control as="select">
                        {
                            options[0].map((item, index) => (
                                <option key={index} >{item}</option>
                            ))
                        }
                    </Form.Control>
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlSelect1">
                    <Form.Label>Your time zone</Form.Label>
                    <Form.Control as="select">
                        {
                            options[1].map((item, index) => (
                                <option key={index} >{item}</option>
                            ))
                        }
                    </Form.Control>
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlSelect1">
                    <Form.Label>Language</Form.Label>
                    <Form.Control as="select">
                        {
                            options[2].map((item, index) => (
                                <option key={index} >{item}</option>
                            ))
                        }
                    </Form.Control>
                </Form.Group>
            </Col>
        </Row>
        <Button variant="danger" style={{float:"right"}}>Save</Button>
        <Application.Base display = {display}>
            <Application.Close toggleDisplay={toggleDisplay}><FontAwesomeIcon icon={faTimes} /></Application.Close>
            <Form onSubmit={handleUpdate} method="POST">
                <Form.Group controlId="username">
                    <Form.Label>Username</Form.Label>
                    <Form.Control type="text" value={username} onChange={({ target }) => setUsername(target.value)}/>
                </Form.Group>

                <Form.Group controlId="formGridEmail">
                    <Form.Label>Email</Form.Label>
                    <Form.Control type="email" value={email} onChange={({ target }) => setEmail(target.value)}/>
                </Form.Group>


                <Form.Group controlId="password">
                    <Form.Label>Phone</Form.Label>
                    <Form.Control type="tel" value={phone} onChange={({ target }) => setPhone(target.value)}/>
                </Form.Group>

                <Button variant="primary" type="submit">
                    Update
                </Button>
            </Form>
        </Application.Base> 
        </>
    )
}

export default Profile
