import { Modal, Button, Image, Row, Col, InputGroup, FormControl, Form} from 'react-bootstrap';
import category1 from '../images/form.png'
import {DB} from '../constants/DB'
import axios from 'axios';

const React = require('react')

class SingleExpense extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            username: '',
            groups: '',
            input: '',
            show: false
        }
        this.handleSearch = this.handleSearch.bind(this)
        this.handleToggle = this.handleToggle.bind(this)
    }

    componentDidMount(){
        let Search_URL = `${DB}/getGroup`
        fetch(Search_URL, {
            method: 'post',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                username: localStorage.getItem('authUser')
            })
        }).then(res => res.json()).then(result=>{
            this.setState({
                groups: result.dataset
            })
        })
    }

    handleSearch(event){
        event.preventDefault();
        let target = event.target;
        if(target.name === "group"){
            this.setState(
                {input: target.value}
            )
        }
    }
    handleToggle() {
        if(this.state.show){
            this.setState({
                show: false
            })
        }else{
            this.setState({
                show: true
            })
        }
        console.log(this.state.show);
    }

    render(){
        let searchGroup = ''
        if(this.state.groups){
            console.log(this.state.groups)
            let newG = this.state.groups.filter((g) => g.name === this.state.input)
            // console.log("this is g", newG);
            searchGroup = newG[0] ? 
            <>
            <Row>
                With you and: 
                <Col sm={2}>
                    <Image src={newG[0].picture} roundedCircle style={{width: "30px", height: "30px"}}/>
                </Col>
                {newG[0].name}
            </Row>

            </> 
            : 
            <Row style={{borderBottom: "1px solid black"}}>
                With you and: <Form.Control type="text" placeholder="Enter group name" onChange={this.handleSearch} value={this.state.input} name="group" style={{width: "70%", marginLeft: "20px"}}/>
            </Row>
        }
        return (
            <>
            <Button variant="primary" onClick={this.handleToggle}>
                Add an expense
            </Button>

            <Modal show={this.state.show} onHide={this.handleToggle} >
                <Modal.Header closeButton>
                <Modal.Title>Add an expense</Modal.Title>
                </Modal.Header>
                <Modal.Body style={{ height: "300px" }}>
                    {searchGroup}
                    <p></p>
                    <Row>
                        <Col md={3}>
                            <Image src={category1} style={{width: "100px"}} rounded />
                        </Col>
                        <Col md={7}>
                            <InputGroup className="mb-3">
                                <FormControl
                                placeholder="Recipient's username"
                                aria-label="Recipient's username"
                                aria-describedby="basic-addon2"
                                />
                            </InputGroup>
                            <InputGroup>
                                <InputGroup.Prepend>
                                <InputGroup.Text>$</InputGroup.Text>
                                </InputGroup.Prepend>
                                <FormControl aria-label="Amount (to the nearest dollar)" />
                                <InputGroup.Append>
                                <InputGroup.Text>.00</InputGroup.Text>
                                </InputGroup.Append>
                            </InputGroup>
                        </Col>
                    </Row>
                </Modal.Body>
                <Modal.Footer>
                <Button variant="secondary" onClick={this.handleToggle}>
                    Cancel
                </Button>
                <Button variant="primary" onClick={this.handleToggle}>
                    Save
                </Button>
                </Modal.Footer>
            </Modal>
            </>
        );
    }
}

export default SingleExpense;
