import { Button, Form, InputGroup, FormControl } from 'react-bootstrap';
import { faFlag, faList, faTag, faUser } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import axios from 'axios';
import logo from '../../images/signup.png'
import Create from '../Create'
import {DB} from '../../constants/DB'
import Application from '../../components/export'

const React = require('react')

class DashNav extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            groups: '',
            currentGroup: ''
        }
        this.getGroup = this.getGroup.bind(this)
    }

    componentDidMount(){
        let group_URL = `${DB}/getGroup`
        const expense = '/expense'
        fetch(group_URL, {
            method: 'post',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                id: localStorage.getItem('authID')
            })
        }).then(res => res.json()).then(result=>{
            console.log("result.dataset: ", result.dataset);
            this.setState({
                groups: result.dataset
            })
        })
    }
    getGroup(event) {
        let text = event.target.innerText;
        let currentGroup = this.state.groups.filter((g) => g.name === text)
        this.props.createItem({
            ID: currentGroup[0].id,
            allGroup: this.state.groups
        });
        console.log("inside getGroup", currentGroup[0].id,);
    }

    render(){
        let displayGroup = this.state.groups ? this.state.groups.map((item, index) => 
        <li className="list-group-item" key={index}>
        <Button variant="link" style={{padding: "0px"}} onClick={this.getGroup}><FontAwesomeIcon icon={faTag} /><strong>{item.name}</strong></Button>
        </li>) 
        : 
        <li className="list-group-item"></li>
        return (
        <>
        <ul className="list-group list-group-flush">
            <li className="list-group-item"><img src={logo} style={{width: "30px"}}/><strong style={{color: "green"}}>Dashboard</strong></li>
            <li className="list-group-item"><FontAwesomeIcon icon={faFlag} /><strong> Recent activity</strong></li>
            <li className="list-group-item"><FontAwesomeIcon icon={faList} /><strong> All expenses</strong></li>
            <Create />
            {displayGroup}
        </ul>
        </>
    );
  }
}

export default DashNav
