import React, {useEffect, useState, useContext} from 'react'
import { Button, Form, Col, Row } from 'react-bootstrap';
import { faPen, faTimes } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import Picture from '../../components/Picture'
import {DB} from '../../constants/DB'
import Application from '../../components/application/applicaiton'
import { UserContext } from '../../contexts/userContext';

function Profile() {
    // const [user, setUser] = useState();

    // const [username, setUsername] = useState();
    // const [email, setEmail] = useState();
    // const [phone, setPhone] = useState();
    // const [picture, setPicture] = useState();
    // const [currency, setCurrency] = useState();
    // const [time, setTime] = useState();
    // const [language, setLanguage] = useState();
    const [display,setDisplay] = useState("none")
    const {
        user, 
        handleChange,
        username, 
        email, 
        phone, 
        currency, 
        time, 
        language} = useContext(UserContext);

    const getAll_URL= `${DB}/getUser`
    const update_URL= `${DB}/update`
    const emailID = localStorage.getItem('authEmail');
    console.log("this is email", emailID);

    function toggleDisplay(){
        if(display === "none"){
            setDisplay("display")
            console.log("this is user: ", user);
            // setUsername(user[0].username)
            // setEmail(user[0].email)
            // setPhone(user[0].phone)
        }
        else
            setDisplay("none")
    }

    useEffect( ()=>{
        try{
            fetch(getAll_URL, {
                method: 'post',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: emailID
                })
            }).then(res => res.json()).then(result=>{
                setUser(result.dataset)
            })
        }catch(e){
            console.log(e);
        }
    },[])
    if(user){
        if(user[0].picture)
            setPicture(user[0].picture)
        if(user[0].currency)
            setCurrency(user[0].currency)
        if(user[0].time)
            setTime(user[0].time)
        if(user[0].language)
            setLanguage(user[0].language)
        console.log("BEFORE INFO: ", username, email, phone, currency, time, language);
    }
    function handleUpdate(){
        toggleDisplay();
        console.log(username, email, phone);
        let newInfo = {
            username: username,
            email: email,
            phone: phone
        }
        console.log("user[0]", user[0]);
        console.log("newInfo", newInfo);
        Object.assign(user[0], newInfo)
    }

    // function handleChange(event){
    //     const value = event.target.value;
    //     const name = event.target.name;
    //     console.log(value, name);
    //     if(name === "currency"){
    //         setCurrency(value);
    //     }
    //     if(name === "time"){
    //         setTime(value);

    //     }
    //     if(name === "language"){
    //         setLanguage(value);
    //     }
    // }

    async function updateAll(){
        try{
            let res = await fetch(update_URL, {
                method: 'post',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    username, username,
                    email: email,
                    phone: phone,
                    currency: currency,
                    time: time,
                    language: language
                })
            });
            let result = await res.json();
            if(result && result.success){
                console.log(result);
            }else if(result && result.success === false){
                alert(result.msg);
            }
        }catch(e){
            console.log(e);
        }

    }


    let title = ["username", "email", "phone"]
    let items = [];
    if(user){
        for (let number = 0; number < 3; number++) {
            items.push(
                <Form.Label>
                    {'Your ' +title[number]}
                    <Button variant="link" onClick={toggleDisplay}><FontAwesomeIcon icon={faPen} />Edit</Button><br />
                    {user[0][title[number]]}
                </Form.Label>
            );
            items.push(<br />)
            items.push(<br />)
        }
    }


    let currencies = []
    let times = []
    let languages= []
    currencies = ['USD', 'CNY'];
    currencies = currencies.map((item, index) => {
        return <option value={item} key={index}>{item}</option>
    });
    times = ['GMT-1', 'GMT-2'];
    times = times.map((item, index) => {
        return <option value={item} key={index}>{item}</option>
    });
    languages = ['English', 'Chinese'];
    languages = languages.map((item, index) => {
        return <option value={item} key={index}>{item}</option>
    });

    return (
        <>
        <Row className="justify-content-md-center">
            <Col sm={2}>
                <Picture />
            </Col>
            <Col sm={2}>
                <Form.Group>
                    {items}
                </Form.Group>
            </Col>
            <Col sm={2}>
                <Form.Group controlId="exampleForm.ControlSelect1">
                    <label htmlFor="currency">Your default currency</label>
                    <select
                        name="currency"
                        id="currency"
                        value={currency}
                        className="form-control"
                        onChange={handleChange}>
                        {currencies}
                    </select>
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlSelect1">
                    <label htmlFor="time">Your time zone</label>
                    <select
                        name="time"
                        id="time"
                        value={time}
                        className="form-control"
                        onChange={handleChange}>
                        {times}
                    </select>
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlSelect1">
                    <label htmlFor="language">Language</label>
                    <select
                        name="language"
                        id="language"
                        value={language}
                        className="form-control"
                        onChange={handleChange}>
                        {languages}
                    </select>
                </Form.Group>
            </Col>
        </Row>
        <Button variant="danger" style={{float:"right"}} onClick={updateAll}>Save</Button>

        <Application.Base display = {display}>
            <Application.Close toggleDisplay={toggleDisplay}><FontAwesomeIcon icon={faTimes} /></Application.Close>
            <Form>
                <Form.Group controlId="username">
                    <Form.Label>Username</Form.Label>
                    <Form.Control type="text" value={username} onChange={({ target }) => setUsername(target.value)}/>
                </Form.Group>

                <Form.Group controlId="formGridEmail">
                    <Form.Label>Email</Form.Label>
                    <Form.Control type="email" value={email} onChange={({ target }) => setEmail(target.value)}/>
                </Form.Group>


                <Form.Group controlId="password">
                    <Form.Label>Phone</Form.Label>
                    <Form.Control type="tel" value={phone} onChange={({ target }) => setPhone(target.value)}/>
                </Form.Group>

                <Button variant="primary" onClick={handleUpdate}>
                    Update
                </Button>
            </Form>
        </Application.Base> 
        </>
    )
}

export default Profile
