import {DB} from '../constants/DB'
const UserContext = React.createContext()
function UserProvider({children}) {
    const [user, setUser] = useState()
    const [username,setUsername] = useState()
    const [email,setEmail] = useState()
    const [phone, setPhone] = useState()
    const [currency,setCurrency] = useState("USD($)")
    const [time,setTIme] = useState("GMT")
    const [languages,setLanguages] = useState("English")

    const getAll_URL= `${DB}/getUser`
    const update_URL= `${DB}/update`
    const emailID = localStorage.getItem('authEmail');
    console.log("this is email", emailID);

    function refreshPage() {
        window.location.reload(false);
    }

    useEffect( ()=>{
        fetch(getAll_URL).then(response=>response.json()).then(result=>setUser(result.dataset))
    },[username, email, phone, picture, currency, time, language])

    // function handleChange(event){
    //     // const target = event.target;
    //     // const value = target.type === 'checkbox' ? target.checked : target.value;
    //     // const name = event.target.name;

    // }

    function handleChange(event){
        const value = event.target.value;
        const name = event.target.name;
        console.log(value, name);
        if(name === "currency"){
            setCurrency(value);
        }
        if(name === "time"){
            setTime(value);
        }
        if(name === "language"){
            setLanguage(value);
        }
    }

    return (
        <>
        <UserContext.Provider  value={{
            user, handleChange,
            }}>
            {children}
        </UserContext.Provider>
        </>
    )
}



