import React, {useState,useContext}from 'react'
import { Button, Form, Col, Row, Jumbotron, Container, Pagination, Tooltip, OverlayTrigger, Popover } from 'react-bootstrap';
import DashNav from '../pages/Dashboard/dashNav'
import {DB} from '../constants/DB'
import Expenses from './Expenses'

function Dashboard() {
    let title = ["total balance", "you owe", "you are owed"]
    let items = [];
    const [group, setGroup] = useState()
    const [expense, setExpense] = useState()

    for (let number = 1; number <= 3; number++) {
        items.push(
            <Pagination.Item key={number} style={{width: "250px"}}>
            {title[number-1]}<br />{number}
            </Pagination.Item>,
        );
    }

    const popover = (
        <Popover id="popover-basic">
            <Popover.Title as="h3">Popover right</Popover.Title>
            <Popover.Content>
            And here's some <strong>amazing</strong> content. It's very engaging.
            right?
            </Popover.Content>
        </Popover>
    );

    function getData(){
        const getExpense_URL = `${DB}/expense/${group}`
        console.log("id", group);
        fetch(getExpense_URL, {
            method: 'post',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                ID: group
            })
        }).then(res => res.json()).then(result=>{
            console.log("result.dataset", result.dataset);
            setExpense(result.dataset)
        })
    }

    const createItem= async(newItem) => {
        setGroup(newItem.ID)
        getData()
    }
    let dashContent = expense ? <Expenses expense={expense}/> : 
    <>
        <Col sm={9}>
            <Row>
                <Jumbotron style={{padding: "10px"}}>
                    <Container>
                        <Row>   
                            <Col sm={8}>
                                <h1>Dashboard</h1>
                            </Col>
                            <Col>
                                <Button variant="danger">Add an expense</Button>
                            </Col>
                            <Pagination size="lg" style={{margin: "0 auto"}}>{items}</Pagination>
                        </Row>
                    </Container>
                </Jumbotron>
            </Row>
            <Row style={{margin: "0 auto"}} className="justify-content-md-center">
                <h3>You owe</h3>
                <OverlayTrigger trigger="click" placement="bottom" overlay={popover}>
                        <Button variant="secondary" className="ml-3">Click me to see</Button>
                    </OverlayTrigger>
                <OverlayTrigger trigger="click" placement="bottom" overlay={popover}>
                    <Button variant="secondary" className="ml-3">Click me to see</Button>
                </OverlayTrigger>{' '}
                <h3>  You are owed</h3>
            </Row>
            <Row className="justify-content-md-center" style={{height: "100%"}}>
                <Col style={{borderRight: "1px solid black", padding: 0}}>
                    <p>hehe</p>
                </Col>
                <Col className="ml-3">
                    <p>hehe</p>
                </Col>
            </Row>
        </Col>
    </> 
    return (
        <>
        <Row>
            <Col sm={3}>
                <DashNav createItem={createItem} />
            </Col>
            {dashContent}
        </Row>

        </>
    )
}

export default Dashboard
