import React, { useState } from 'react'
import { Modal, Button, Image, Row, Col, InputGroup, FormControl} from 'react-bootstrap';
import category1 from '../images/form.png'
import {DB} from '../constants/DB'

function SingleExpense() {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const group_URL = `${DB}/getGroup`
    const [groups,setGroups] = useState();
    const [input, setInput] = useState();

    useEffect( ()=>{
        try{
            fetch(group_URL, {
                method: 'post',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    username: localStorage.getItem('authUser')
                })
            }).then(res => res.json()).then(result=>{
                setGroups(result.dataset)
            })
        }catch(e){
            console.log(e);
        }
    },[])

    function handleSearch(event){
        event.preventDefault();
        let target = event.target;
        if(target.name === "group"){
            setInput(target.value)
        }
    }

    let searchGroup = ''
        if(groups){
            console.log(groups)
            let newG = groups.filter((g) => g.name === input)
            // console.log("this is g", newG);
            searchGroup = newG[0] ? 
            <>
            <Row>
                With you and: 
                <Col sm={2}>
                    <Image src={newG[0].picture} roundedCircle style={{width: "30px", height: "30px"}}/>
                </Col>
                <div style={{width: "100px"}}>{newG[0].name}</div>
            </Row>
            </> 
            : 
            <Row style={{borderBottom: "1px solid black"}}>
                With you and: <Form.Control type="text" placeholder="Enter group name" onChange={this.handleSearch} value={this.state.input} name="group" style={{width: "70%", marginLeft: "20px"}}/>
            </Row>
        }

    return (
        <>
            <Button variant="primary" onClick={handleToggle}>
                Add an expense
            </Button>

            <Modal show={show} onHide={handleToggle} >
                <Modal.Header closeButton>
                <Modal.Title>Add an expense</Modal.Title>
                </Modal.Header>
                <Modal.Body style={{ height: "300px" }}>
                    {searchGroup}
                    <p></p>
                    <Row>
                        <Col md={3}>
                            <Image src={category1} style={{width: "100px"}} rounded />
                        </Col>
                        <Col md={7}>
                            <InputGroup className="mb-3">
                                <FormControl
                                placeholder="Recipient's username"
                                aria-label="Recipient's username"
                                aria-describedby="basic-addon2"
                                />
                            </InputGroup>
                            <InputGroup>
                                <InputGroup.Prepend>
                                <InputGroup.Text>$</InputGroup.Text>
                                </InputGroup.Prepend>
                                <FormControl aria-label="Amount (to the nearest dollar)" />
                                <InputGroup.Append>
                                <InputGroup.Text>.00</InputGroup.Text>
                                </InputGroup.Append>
                            </InputGroup>
                        </Col>
                    </Row>
                </Modal.Body>
                <Modal.Footer>
                <Button variant="secondary" onClick={handleToggle}>
                    Cancel
                </Button>
                <Button variant="primary" onClick={handleToggle}>
                    Save
                </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}

export default SingleExpense;
