        user, 
        handleChange,
        username, 
        email, 
        phone, 
        currency, 
        time, 
        language} = useContext(UserContext);

    const getAll_URL= `${DB}/getUser`
    const update_URL= `${DB}/update`
    const emailID = localStorage.getItem('authEmail');
    console.log("this is user", user);

    function toggleDisplay(){
        if(display === "none"){
            setDisplay("display")
            console.log("this is user: ", user);
        }
        else
            setDisplay("none")
    }

    // useEffect( ()=>{
    //     try{
    //         fetch(getAll_URL, {
    //             method: 'post',
    //             headers: {
    //                 'Accept': 'application/json',
    //                 'Content-Type': 'application/json'
    //             },
    //             body: JSON.stringify({
    //                 email: emailID
    //             })
    //         }).then(res => res.json()).then(result=>{
    //             setUser(result.dataset)
    //         })
    //     }catch(e){
    //         console.log(e);
    //     }
    // },[])
    // if(user){
    //     if(user[0].picture)
    //         setPicture(user[0].picture)
    //     if(user[0].currency)
    //         setCurrency(user[0].currency)
    //     if(user[0].time)
    //         setTime(user[0].time)
    //     if(user[0].language)
    //         setLanguage(user[0].language)
    //     console.log("BEFORE INFO: ", username, email, phone, currency, time, language);
    // }
    function handleUpdate(){
        toggleDisplay();
        console.log(username, email, phone);
        let newInfo = {
            username: username,
            email: email,
            phone: phone
        }
        console.log("user[0]", user[0]);
        console.log("newInfo", newInfo);
        Object.assign(user[0], newInfo)
    }

    // function handleChange(event){
    //     const value = event.target.value;
    //     const name = event.target.name;
    //     console.log(value, name);
    //     if(name === "currency"){
    //         setCurrency(value);
    //     }
    //     if(name === "time"){
    //         setTime(value);

    //     }
    //     if(name === "language"){
    //         setLanguage(value);
    //     }
    // }

    async function updateAll(){
        try{
            let res = await fetch(update_URL, {
                method: 'post',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    username, username,
                    email: email,
                    phone: phone,
                    currency: currency,
                    time: time,
                    language: language
                })
            });
            let result = await res.json();
            if(result && result.success){
                console.log(result);
            }else if(result && result.success === false){
                alert(result.msg);
            }
        }catch(e){
            console.log(e);
        }

    }

    let currencies = []
    let times = []
    let languages= []
    currencies = ['USD', 'CNY'];
    currencies = currencies.map((item, index) => {
        return <option value={item} key={index}>{item}</option>
    });
    times = ['GMT-1', 'GMT-2'];
    times = times.map((item, index) => {
        return <option value={item} key={index}>{item}</option>
    });
    languages = ['English', 'Chinese'];
    languages = languages.map((item, index) => {
        return <option value={item} key={index}>{item}</option>
    });

    if(user){
        return (
        <>
        <Row className="justify-content-md-center">
            <Col sm={2}>
                <Picture />
            </Col>
            <Col sm={2}>
                <Form.Group>
                    <Form.Label>
                    Your name
                    <Button variant="link" onClick={toggleDisplay}><FontAwesomeIcon icon={faPen} />Edit</Button><br />
                    {user[0].username}
                </Form.Label>
                </Form.Group>
                <Form.Group>
                    <Form.Label>
                    Your email
                    <Button variant="link" onClick={toggleDisplay}><FontAwesomeIcon icon={faPen} />Edit</Button><br />
                    {user[0].email}
                </Form.Label>
                </Form.Group>
                <Form.Group>
                    <Form.Label>
                    Your phone
                    <Button variant="link" onClick={toggleDisplay}><FontAwesomeIcon icon={faPen} />Edit</Button><br />
                    {user[0].phone}
                </Form.Label>
                </Form.Group>
            </Col>
            <Col sm={2}>
                <Form.Group controlId="exampleForm.ControlSelect1">
                    <label htmlFor="currency">Your default currency</label>
                    <select
                        name="currency"
                        id="currency"
                        value={currency}
                        className="form-control"
                        onChange={handleChange}>
                        {currencies}
                    </select>
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlSelect1">
                    <label htmlFor="time">Your time zone</label>
                    <select
                        name="time"
                        id="time"
                        value={time}
                        className="form-control"
                        onChange={handleChange}>
                        {times}
                    </select>
                </Form.Group>
                <Form.Group controlId="exampleForm.ControlSelect1">
                    <label htmlFor="language">Language</label>
                    <select
                        name="language"
                        id="language"
                        value={language}
                        className="form-control"
                        onChange={handleChange}>
                        {languages}
                    </select>
                </Form.Group>
            </Col>
        </Row>
        <Button variant="danger" style={{float:"right"}} onClick={updateAll}>Save</Button>

        <Application.Base display = {display}>
            <Application.Close toggleDisplay={toggleDisplay}><FontAwesomeIcon icon={faTimes} /></Application.Close>
            <Form>
                <Form.Group controlId="username">
                    <Form.Label>Username</Form.Label>
                    <Form.Control type="text" value={username} onChange={handleChange} name="username" />
                </Form.Group>

                <Form.Group controlId="email">
                    <Form.Label>Email</Form.Label>
                    <Form.Control type="email" value={email} onChange={handleChange} name="email" />
                </Form.Group>


                <Form.Group controlId="phone">
                    <Form.Label>Phone</Form.Label>
                    <Form.Control type="tel" value={phone} onChange={handleChange} name="phone" />
                </Form.Group>

                <Button variant="primary" onClick={handleUpdate}>
                    Update
                </Button>
            </Form>
        </Application.Base> 
        </>
    )
    }else{
        return <div>Loading...</div>
    }
}

export default Profile
