import {DB} from '../constants/DB'
import axios from 'axios'
import {SET_CURRENT_USER} from '../constants/constant'

export const setCurrentUser = (user) => {
    return{
        type: SET_CURRENT_USER,
        user
    }
}

export const login = (userData) =>{
    return dispatch => {
        return axios.post(`${DB}/signin`, userData).then(res=>{
            localStorage.setItem('authUser', res.data.username);
            localStorage.setItem('authEmail', res.data.email);
            localStorage.setItem('authID', res.data.ID);
            dispatch(setCurrentUser(res.data))
        });
    }
}
