import React, {useEffect, useState} from 'react'
import { Button, Form, InputGroup, FormControl } from 'react-bootstrap';
import logo from '../../images/signup.png'
import { faFlag, faList, faTag, faUser } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import Create from '../Create'
import {DB} from '../../constants/DB'

function DashNav() {
    const group_URL = `${DB}/getGroup`
    const [groups,setGroups] = useState();
    const expense = '/expense'
    useEffect( ()=>{
        try{
            fetch(group_URL, {
                method: 'post',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    id: localStorage.getItem('authID')
                })
            }).then(res => res.json()).then(result=>{
                // setUser(result.dataset)
                console.log("result.dataset: ", result.dataset);
                setGroups(result.dataset)
                console.log(groups);
            })
        }catch(e){
            console.log(e);
        }
    },[])
    // let displayGroup = ''
    // import {Link} from "react-router-dom"
    // <Link to={`${expense}/${groups[g].id}`}><FontAwesomeIcon icon={faTag} /><strong>{groups[g].name}</strong></Link>

    let displayGroup = groups ? groups.map((index, g) => 
    <li className="list-group-item" key={index}>
        <a href={`${expense}/${groups[g].id}` }><FontAwesomeIcon icon={faTag} /><strong>{groups[g].name}</strong></a>
    </li>) : <li className="list-group-item"></li>
    return (

        <>
        <ul className="list-group list-group-flush">
            <li className="list-group-item"><img src={logo} style={{width: "30px"}}/><strong style={{color: "green"}}>Dashboard</strong></li>
            <li className="list-group-item"><FontAwesomeIcon icon={faFlag} /><strong> Recent activity</strong></li>
            <li className="list-group-item"><FontAwesomeIcon icon={faList} /><strong> All expenses</strong></li>
            <Create />
            {displayGroup}
            <InputGroup className="mb-3">
                <FormControl
                placeholder="FRIENDS"
                aria-label="friends"
                aria-describedby="basic-addon2"
                />
                <InputGroup.Append>
                <Button variant="outline-secondary">+ add</Button>
                </InputGroup.Append>
            </InputGroup>
            <li className="list-group-item"><FontAwesomeIcon icon={faUser} /><strong> Friend 1</strong></li>
            <li className="list-group-item"><FontAwesomeIcon icon={faUser} /><strong> Friend 2</strong></li>
            <li className="list-group-item"><FontAwesomeIcon icon={faUser} /><strong> Friend 3</strong></li>
        </ul>
        </>
    )
}

export default DashNav
