import React, {useState,useEffect,useContext}from 'react'
import { Button, Form, Col, Row, Jumbotron, Container, Pagination, Tooltip, OverlayTrigger, Popover } from 'react-bootstrap';
import DashNav from '../pages/Dashboard/dashNav'
import {DB} from '../constants/DB'
import Expenses from './Expenses'
import {UserContext} from '../contexts/userContext'
import {GroupContext} from '../contexts/groupContext'


function Dashboard() {
    let title = ["total balance", "you owe", "you are owed"]
    let items = [];
    const [group, setGroup] = useState()
    const [expense, setExpense] = useState()
    const [userInfo, setUserInfo] = useState()
    const [owe, setOwe] = useState()
    const [owed, setOwed] = useState()
    const [total, setTotal] = useState()
    const {user} = useContext(UserContext);
    const {groups} = useContext(GroupContext);
    // for (let number = 1; number <= 3; number++) {
    //     items.push(
    //         <Pagination.Item key={number} style={{width: "250px"}}>
    //         {title[number-1]}<br />{number}
    //         </Pagination.Item>,
    //     );
    // }

    useEffect( ()=>{
        let user_URL = `${DB}/getInfo`
        try{
            fetch(user_URL, {
                method: 'post',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    ID: localStorage.getItem('authID')
                })
            }).then(res => res.json()).then(result=>{
                console.log("userInfo: ", result.dataset);
                setUserInfo(result.dataset)
            })
        }catch(e){
            console.log(e);
        }
    },[])

    const popover = (
        <Popover id="popover-basic">
            <Popover.Title as="h3">Popover right</Popover.Title>
            <Popover.Content>
            And here's some <strong>amazing</strong> content. It's very engaging.
            right?
            </Popover.Content>
        </Popover>
    );

    function getData(){
        const getExpense_URL = `${DB}/expense/${group}`
        console.log("id", group);
        fetch(getExpense_URL, {
            method: 'post',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                ID: group
            })
        }).then(res => res.json()).then(result=>{
            console.log("result.dataset", result.dataset);
            setExpense(result.dataset)
        })
    }

    const createItem= async(newItem) => {
        setGroup(newItem.ID)
        getData()
    }

    if(groups){
        console.log("if groups", );
    }

    let groupInfo = []
    let oneGroup = []
    let totalOwed = []
    if(userInfo&&user&&groups){
        console.log("inside", userInfo);
        let one = 0
        let two = 20
        let three = 0
        // let groupCost = 0
        for(let i = 0; i < userInfo.length; i++){
            // const areOwedUser = userInfo[i].others.split(',')
            // console.log("each owed", userInfo.expense, areOwedUser.length);
            // console.log("userName", userName);
            // console.log("groupName", groupName);
            // let oneBill = Number((userInfo[i].expense - (userInfo[i].expense / (areOwedUser.length+1))).toFixed(2))
            // three += oneBill;
            let eachOwed = userInfo[i].amount
            // let groupName = groups.filter(g=>g.G_ID===userInfo[i].G_ID)
            let userName = user.filter(u=>u.ID===parseInt(userInfo[i].user))

            oneGroup.push(
                <>
                <Row>
                    <img src={userName[0].picture} alt="" style={{width:"30px", height: "30px"}}/>    
                    <div>{userName[0].username} owes you {eachOwed} for "{userInfo[i].description}"</div>
                </Row>
                <p></p>
                </>
            )
            three += eachOwed
        }

        groupInfo.push(oneGroup);
        // console.log("one", one);
        totalOwed.push(
            <>
                <Pagination.Item key="1" style={{width: "250px"}}>
                    {title[2]}<br />{Number(three-two).toFixed(2)}
                </Pagination.Item>
                <Pagination.Item key="2" style={{width: "250px"}}>
                    {title[2]}<br />{Number(two).toFixed(2)}
                </Pagination.Item>
                <Pagination.Item key="3" style={{width: "250px"}}>
                    {title[2]}<br />{three}
                </Pagination.Item>
            </>
        )
    }

    let dashContent = expense ? <Expenses expense={expense}/> : 
    <>
        <Col sm={9}>
            <Row>
                <Jumbotron style={{padding: "10px"}}>
                    <Container>
                        <Row>   
                            <Col sm={8}>
                                <h1>Dashboard</h1>
                            </Col>
                            <Col>
                                <Button variant="danger">Settle up</Button>
                            </Col>
                            <Pagination size="lg" style={{margin: "0 auto"}}>
                                {totalOwed}
                            </Pagination>
                        </Row>
                    </Container>
                </Jumbotron>
            </Row>
            <Row style={{margin: "0 auto"}} className="justify-content-md-center">
                <h3>You owe</h3>
                <OverlayTrigger trigger="click" placement="bottom" overlay={popover}>
                <Button variant="secondary" className="ml-3">Click me to see</Button>
                </OverlayTrigger>
                <OverlayTrigger trigger="click" placement="bottom" overlay={popover}>
                    <Button variant="secondary" className="ml-3">Click me to see</Button>
                </OverlayTrigger>{' '}
                    <h3>You are owed</h3>
            </Row>
            <Row className="justify-content-md-center" style={{height: "100%"}}>
                <Col style={{borderRight: "1px solid black", padding: 0}}>
                    <p>hehe</p>
                </Col>
                <Col className="ml-3">
                    <p></p>
                    {groupInfo}
                </Col>
            </Row>
        </Col>
    </> 
    return (
        <>
        <Row>
            <Col sm={3}>
                <DashNav createItem={createItem} />
            </Col>
            {dashContent}
        </Row>

        </>
    )
}

export default Dashboard
