import { Row, Image, Form, Col } from 'react-bootstrap';
import logo from '../images/signup.png'

const React = require('react')

class CreateInput extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            username: '',
            email: ''
        }
        // this.handleChange = this.handleChange.bind(this)
    }

    // handleChange(event) {
    //     event.preventDefault();
    //     console.log(event);
    //     console.log(event.target);
    //     console.log(event.target.username, " and ", event.target.email);
    //     this.setState({
    //         username: event.target.username,
    //         email: event.target.email
    //     })
    // }
    render(){
        return (
        <>
            <Row>
                    <Col sm={2}>
                        <Image src={logo} roundedCircle style={{width: "30px"}}/>
                    </Col>
                    <Col sm={4}>
                        <Form.Control type="text" placeholder="Enter username" onChange={({ target }) => this.setState({username: target.value})} value={this.state.username}/>
                        {this.state.username}
                    </Col>
                    <Col sm={5}>
                        <Form.Control type="email" placeholder="Enter email" onChange={({ target }) => this.setState({email: target.value})} value={this.state.email}/>
                        {this.state.email}
                    </Col>

                </Row>
        </>
    );
  }
}

export default CreateInput
