import { Modal, Button, Form, InputGroup, FormControl,Row } from 'react-bootstrap';
import { faFlag, faList, faTag, faUser } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import axios from 'axios';
import logo from '../../images/signup.png'
import Create from '../Create'
import {DB} from '../../constants/DB'
import {Application} from '../../components/export'

const React = require('react')

class DashNav extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            groups: '',
            currentGroup: '',
            show: true
        }
        this.getGroup = this.getGroup.bind(this)
        this.handleCancel = this.handleCancel.bind(this)
        this.handleAccept = this.handleSubmit.bind(this)
    }

    componentDidMount(){
        let group_URL = `${DB}/getGroup`
        const expense = '/expense'
        fetch(group_URL, {
            method: 'post',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                id: localStorage.getItem('authID')
            })
        }).then(res => res.json()).then(result=>{
            console.log("result.dataset: ", result.dataset);
            this.setState({
                groups: result.dataset
            })
        })
    }
    getGroup(event) {
        let text = event.target.innerText;
        let currentGroup = this.state.groups.filter((g) => g.name === text)
        this.props.createItem({
            ID: currentGroup[0].id,
            allGroup: this.state.groups
        });
        console.log("inside getGroup", currentGroup[0].id,);
    }   
    handleCancel(){
        console.log("cancel this invitation");
    }

    handleAccept(e){
        console.log(e.target.id);
        console.log("accpet this invitation");
    }

    render(){
        let displayGroup = this.state.groups ? this.state.groups.map((item, index) => 
        <li className="list-group-item" key={index}>
        <Button variant="link" style={{padding: "0px"}} onClick={this.getGroup}><FontAwesomeIcon icon={faTag} /><strong>{item.name}</strong></Button>
        </li>) 
        : 
        <li className="list-group-item"></li>
        let invitation =[]
        let itemList = []
        console.log("this.state.groups", this.state.groups);
        invitation = this.state.groups ? 
            <>
        <Modal show={this.state.show} onHide={this.handleClose} >
            <Modal.Header closeButton>
            <Modal.Title>Invitation</Modal.Title>
            </Modal.Header>
            <Modal.Body style={{ height: "300px" }}>
                {
                this.state.groups.forEach((item, index) => {
                    if(item.invitation!==1){
                        itemList.push(
                            <>
                            <Row key={index}>
                                <h5>Having a invitation from "{item.name}"</h5>
                                <Button variant="success" className="ml-3" id={index} onClick={this.handleAccept}>accpet</Button >
                                <Button variant="danger" className="ml-3" id={index} onClick={this.handleCancel}>cancel</Button > 
                            </Row>
                            <p></p>
                            </>
                            )
                    }
                })
                }
                {itemList}
            </Modal.Body>
            <Modal.Footer>
            <Button variant="secondary" onClick={this.handleClose}>
                Cancel
            </Button>
            <Button variant="primary" onClick={this.handleSubmit}>
                Accpet
            </Button>
            </Modal.Footer>
        </Modal>
            </> : null;
        return (
        <>
        <ul className="list-group list-group-flush">
            <li className="list-group-item"><img src={logo} style={{width: "30px"}}/><strong style={{color: "green"}}>Dashboard</strong></li>
            <li className="list-group-item"><FontAwesomeIcon icon={faFlag} /><strong> Recent activity</strong></li>
            <li className="list-group-item"><FontAwesomeIcon icon={faList} /><strong> All expenses</strong></li>
            <Create />
            {displayGroup}
            {invitation}
        </ul>
        </>
    );
  }
}

export default DashNav
