import React from 'react'
import { Row, Image, Form, Col } from 'react-bootstrap';
import logo from '../images/form.png'

function Balance(props) {
    console.log("balance");
    let userTotal;
    if(props.userInfo)
        userTotal = props.userInfo.map(info => <>
            <Col md={3}>
            <Image src={info.picture} roundedCircle style={{width: "40px"}}/>
            </Col>
            <Col>
                <Row>
                    <h5>{info.username}</h5>
                </Row>
                <Row>
                    {
                    info.total > 0 ?
                    <h4><span>owes</span>USD ${info.total}</h4> : <h4><span>gets back</span>USD ${info.total}</h4>
                    }
                </Row>
            </Col>
        </>)
    return (
        <>
        <Row>
            {userTotal}
        </Row>
        </>
    )
}

export default Balance
