import React, {useEffect, useState} from 'react'
import { Button, Form, Col, Row } from 'react-bootstrap';
import { faPen } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import Picture from '../../components/Picture'
import {DB} from '../../constants/DB'

function Profile() {
    const [user, setUser] = useState();
    const getAll_URL= `${DB}/getUser`
    const email = localStorage.getItem('authEmail');
    console.log("this is email", email);
    useEffect( ()=>{
        try{
            fetch(getAll_URL, {
                method: 'post',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: email
                })
            }).then(res => res.json()).then(result=>{
                setUser(result.dataset)
            })
        }catch(e){
            console.log(e);
        }
    },[])

    console.log("this is user info: ", user);

    let title = ["Your name", "Your email address", "Your phone number"]
    let items = [];
    for (let number = 0; number < 3; number++) {
        items.push(
            <Form.File.Label>
                {title[number]}
                <a href="#"><FontAwesomeIcon icon={faPen} />Edit</a><br />
                {user[0][number]}
            </Form.File.Label>
        );
        items.push(<br />)
        items.push(<br />)
    }

    let others = ["Your default currency", "Your time zone", "Language"]
    let other_items = [];
    for (let number = 1; number <= 3; number++) {
        other_items.push(
            <Form.Group controlId="exampleForm.ControlSelect1">
                <Form.Label>{others[number-1]}</Form.Label>
                <Form.Control as="select">
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
                </Form.Control>
            </Form.Group>
        );
    }

    return (
        <>
        <Row className="justify-content-md-center">
            <Col sm={2}>
                <Picture />
            </Col>
            <Col sm={2}>
                <Form.Group>
                    {items}
                </Form.Group>
            </Col>
            <Col sm={2}>
                <Form.Group>
                    {other_items}
                </Form.Group>
            </Col>
        </Row>
        <Button variant="danger" style={{float:"right"}}>Save</Button>
        </>
    )
}

export default Profile
