import { Row, Image, Form, Col } from 'react-bootstrap';
import logo from '../images/signup.png'
import {DB} from '../constants/DB'

const React = require('react')

class CreateInput extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            username: '',
            email: ''
        }
        this.handleSearch = this.handleSearch.bind(this)
    }

    // handleChange(event) {
    //     event.preventDefault();
    //     console.log(event);
    //     console.log(event.target);
    //     console.log(event.target.username, " and ", event.target.email);
    //     this.setState({
    //         username: event.target.username,
    //         email: event.target.email
    //     })
    // }
    Search_URL = `${DB}/searchUser`
    handleSearch(event){
        event.preventDefault();
        try{
            let res = fetch(Login_URL, {
                method: 'post',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: this.state.email,
                    username: this.state.password
                })
            });
            let result = res.json();

            if(result && result.success){
                console.log(result);

                console.log("get one user");
            }else if(result && result.success === false){
                alert(result.msg);
            }
        }catch(e){
            console.log(e);
        }
    }

    render(){
        return (
        <>
            <Row>
                    <Col sm={2}>
                        <Image src={logo} roundedCircle style={{width: "30px"}}/>
                    </Col>
                    <Col sm={4}>
                        <Form.Control type="text" placeholder="Enter username" onChange={({ target }) => this.setState({username: target.value})} value={this.state.username} onKeyPressCapture={this.handleSearch}/>
                        {this.state.username}
                    </Col>
                    <Col sm={5}>
                        <Form.Control type="email" placeholder="Enter email" onChange={({ target }) => this.setState({email: target.value})} value={this.state.email} onKeyPressCapture={this.handleSearch}/>
                        {this.state.email}
                    </Col>

                </Row>
        </>
    );
  }
}

export default CreateInput
