import React, {useState} from 'react'
import { Button, Form } from 'react-bootstrap';
import { Application } from '../../components/export';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTimes} from '@fortawesome/free-solid-svg-icons'
import {DB} from '../../constants/DB'

function Signin() {
    const [display,setDisplay] = useState("none")
    const [username, setuUsername] = useState('');
    const [password, setPassword] = useState('');

    const Login_URL = `${DB}/signin`


    function toggleDisplay(){
        if(display === "none")
            setDisplay("display")
        else
            setDisplay("none")
    }

    async function handleSignin(event){
        event.preventDefault();
        if(!username){
            return;
        }
        if(!password){
            return;
        }
        try{
            let res = await fetch(Login_URL, {
                method: 'post',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    username: username,
                    password: password
                })
            });
            let result = await res.json();

            if(result && result.success){
                if(result && result.token ){
                    var token = result.token;
                    var decoded = jwt_decode(token);
                    localStorage.setItem('authUser', JSON.stringify(decoded.user));
                    setUser(JSON.parse(localStorage.getItem('authUser')));
                   const U = JSON.parse(localStorage.getItem('authUser'))

                    if(U.role === "A"){
                        history.push(ROUTES.HELP);

                    }
                    else{
                        history.push(ROUTES.HOME);

                    }
                    refreshPage()
                }

                console.log("login success");
            }else if(result && result.success === false){
                alert(result.msg);
            }
        }catch(e){
            console.log(e);
        }
    }


    return (    
        <>
        <Button variant="primary" onClick={toggleDisplay}>Sign In</Button>
        <Application.Base display = {display}>
            <Application.Close toggleDisplay={toggleDisplay}><FontAwesomeIcon icon={faTimes} /></Application.Close>
            <Form onSubmit={handleSignin}>
                <Form.Group controlId="username">
                    <Form.Label>Username</Form.Label>
                    <Form.Control type="text" placeholder="Enter username" onChange={({ target }) => setuUsername(target.value)}/>
                    <Form.Text className="text-muted">
                    We'll never share your info with anyone else.
                    </Form.Text>
                </Form.Group>

                <Form.Group controlId="password">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" placeholder="Password" onChange={({ target }) => setPassword(target.value)}/>
                </Form.Group>
                <Form.Text className="text-muted">Haven't have account? 
                    <Button variant="link">
                            Sign me in!
                    </Button>
                </Form.Text>
                <Button variant="primary" type="submit">
                    Submit
                </Button>
            </Form>
        </Application.Base>  
        </>
    )
}

export default Signin
