        //     }else if(result && result.success === false){
        //         alert(result.msg);
        //     }
        // }catch(e){
        //     console.log(e);
        // }
    }

    render(){
        if(this.state.users){
            console.log(this.state.users);
            let user = this.state.users.filter(u => u.username === this.state.username)
            console.log("this is user: ", user);
        }
        return (
        <>
            <Row>
                    <Col sm={2}>
                        <Image src={logo} roundedCircle style={{width: "30px"}}/>
                    </Col>
                    <Col sm={4}>
                        <Form.Control type="text" placeholder="Enter username" onChange={this.handleSearch} value={this.state.username} name="username"/>
                        {this.state.username}
                    </Col>
                    <Col sm={5}>
                        <Form.Control type="email" placeholder="Enter email" onChange={this.handleSearch} value={this.state.email} name="email"/>
                        {this.state.email}
                    </Col>

                </Row>
        </>
    );
  }
}

export default CreateInput
